targets.query:
from(bucket: "mult_source_heatpump")
   |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
   |> filter(fn: (r) => r._measurement == "temperature" or r._measurement == "flow")
   |> filter(fn: (r) => r._field == "WATER_IN_COND" or r._field == "WATER_OUT_COND" or r._field == "FLOW_CONDENSER")
   |> keep(columns: ["_time", "_field", "_value", "FOUR_WAY_VALVE"])
   |> group(columns: ["_time"], mode: "by")
   |> pivot(rowKey: ["_time", "FOUR_WAY_VALVE"], columnKey: ["_field"], valueColumn: "_value")
   |> map(
       fn: (r) => ({r with Q_CON_HEAT:
           if r.FOUR_WAY_VALVE == "True" then 4.18*(998.0/3600.0*r.FLOW_CONDENSER)*(r.WATER_OUT_COND-r.WATER_IN_COND)
           else 0.0 }),
    )
   |> keep(columns: ["_time", "Q_CON_HEAT"])
   |> group()
   |> aggregateWindow(every: v.windowPeriod, fn: max, column: "Q_CON_HEAT")
   |> keep(columns: ["_time", "Q_CON_HEAT"])

